EXP_NS01.SQL:
select k.DEPARTURE_DATE, k.KE as KE_CARTON, NVL(x.KE, 0) as KE_SKID, (k.KE + NVL(x.KE, 0)) as KE_TOTAL, k.EK as EK_CARTON, x.EK as EK_SKID, (k.EK + x.EK) as EK_TOTAL, k.CX as CX_CARTON, x.CX as CX_SKID, (k.CX + x.CX) as CX_TOTAL, k.CI as CI_CARTON, x.CI as CI_SKID, (k.CI + x.CI) as CI_TOTAL, k.JL as JL_CARTON, x.JL as JL_SKID, (k.JL + x.JL) as JL_TOTAL, k.XX as XX_CARTON, x.XX as XX_SKID, (k.XX + x.XX) as XX_TOTAL, k.VN as VN_CARTON, x.VN as VN_SKID, (k.VN + x.VN) as VN_TOTAL, k.ET as ET_CARTON, x.ET as ET_SKID, (k.ET + x.ET) as ET_TOTAL, NVL(K.VZ, 0) as VZ_CARTON, x.VZ as VZ_SKID, ( NVL(K.VZ, 0) + x.VZ) as VZ_TOTAL, k.OT as OT_CARTON, x.OT as OT_SKID, (k.OT + x.OT) as OT_TOTAL from (select * from (select t.DEPARTURE_DATE,t.FLIGHT_NO --sum(t.DEPARTED_PIECES) as Pices , sum( NVL(t.DEPARTED_WEIGHT, 0)) as Weight from ( SELECT to_char(to_date('02-01-0001' ,'DD-MM-YYYY') + flup.flup_estimated_date, 'DD/MM/YYYY') AS DEPARTURE_DATE, case when flup.FLUP_FLIGHT_NO_LVG in ('CI','CX','EK','ET','JL','KE','5X','VN','VZ','KA','TK','LJ','LD') then flup.FLUP_FLIGHT_NO_LVG else 'Other' end as FLIGHT_NO , labs.labs_mawb_prefix||labs.labs_mawb_serial_no as MAWB, sum(awbu.awbu_pieces) as DEPARTED_PIECES, sum( awbu.AWBU_WEIGHT) as DEPARTED_WEIGHT, case when sum( awbu.AWBU_WEIGHT) / sum(awbu.awbu_pieces) > 30 then 'SKID' else 'Carton' end as Cargo_Type
FROM FLUP flup JOIN CONT cont ON cont.CONT_FLIGHT_NO_ = flup.flup_flight_no and to_date('02-01-0001' ,'DD-MM-YYYY') + cont.CONT_DATE = to_date('02-01-0001' ,'DD-MM-YYYY') + flup.flup_scheduled_date JOIN AWBU_AWBPERULD_LIST awbu on awbu.awbu_uld_isn = cont.cont_uld_isn JOIN LABS labs on awbu.awbu_mawb_ident_no = labs.LABS_IDENT_NO WHERE to_date('02-01-0001' ,'DD-MM-YYYY') + flup.flup_estimated_date between to_date('{0}', 'dd/mm/yyyy') and to_date('{1}', 'dd/mm/yyyy') AND labs.labs_deleted = 0 and awbu.awbu_pieces > 0 --and flup.flup_flight_no_lvg || flup.flup_flight_no = 'KE680' group by flup.flup_estimated_date, flup.FLUP_FLIGHT_NO_LVG,flup.flup_flight_no , labs.labs_mawb_prefix,labs.labs_mawb_serial_no)t where t.Cargo_Type = 'Carton' group by t.DEPARTURE_DATE,t.FLIGHT_NO,t.Cargo_Type)m pivot ( sum(m.Weight) for FLIGHT_NO in ( 'KE' AS KE, 'EK' as EK, 'CX' as CX, 'CI' as CI, 'JL' as JL, '5X' as XX, 'VN' as VN, 'ET' as ET, 'VZ' as VZ, 'TH' as TH, 'VJ' as VJ, 'Other' as OT
) ) ORDER BY 1)k left join (select * from (select t.DEPARTURE_DATE,t.FLIGHT_NO --sum(t.DEPARTED_PIECES) as Pices ,sum(t.DEPARTED_WEIGHT) as Weight from ( SELECT to_char(to_date('02-01-0001' ,'DD-MM-YYYY') + flup.flup_estimated_date, 'DD/MM/YYYY') AS DEPARTURE_DATE, case when flup.FLUP_FLIGHT_NO_LVG in ('CI','CX','EK','ET','JL','KE','5X','VN','VZ','KA','TK','LJ','LD') then flup.FLUP_FLIGHT_NO_LVG else 'Other' end as FLIGHT_NO , labs.labs_mawb_prefix||labs.labs_mawb_serial_no as MAWB, sum(awbu.awbu_pieces) as DEPARTED_PIECES, sum( awbu.AWBU_WEIGHT) as DEPARTED_WEIGHT, case when sum( awbu.AWBU_WEIGHT) / sum(awbu.awbu_pieces) > 30 then 'SKID' else 'Carton' end as Cargo_Type
FROM FLUP flup JOIN CONT cont ON cont.CONT_FLIGHT_NO_ = flup.flup_flight_no and to_date('02-01-0001' ,'DD-MM-YYYY') + cont.CONT_DATE = to_date('02-01-0001' ,'DD-MM-YYYY') + flup.flup_scheduled_date JOIN AWBU_AWBPERULD_LIST awbu on awbu.awbu_uld_isn = cont.cont_uld_isn JOIN LABS labs on awbu.awbu_mawb_ident_no = labs.LABS_IDENT_NO WHERE to_date('02-01-0001' ,'DD-MM-YYYY') + flup.flup_estimated_date between to_date('{0}', 'dd/mm/yyyy') and to_date('{1}', 'dd/mm/yyyy') AND labs.labs_deleted = 0 and awbu.awbu_pieces > 0 --and flup.flup_flight_no_lvg || flup.flup_flight_no = 'KE680' group by flup.flup_estimated_date, flup.FLUP_FLIGHT_NO_LVG,flup.flup_flight_no , labs.labs_mawb_prefix,labs.labs_mawb_serial_no)t where t.Cargo_Type = 'SKID' group by t.DEPARTURE_DATE,t.FLIGHT_NO,t.Cargo_Type)a pivot ( sum(a.Weight) for FLIGHT_NO in ( 'KE' AS KE, 'EK' as EK, 'CX' as CX, 'CI' as CI, 'JL' as JL, '5X' as XX, 'VN' as VN, 'ET' as ET, 'VZ' as VZ, 'TH' as TH, 'VJ' as VJ, 'Other' as OT
) ) ORDER BY 1)x on k.DEPARTURE_DATE= x.DEPARTURE_DATE order by k.DEPARTURE_DATE
